import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType } from "../../models/Question";
import { shortenLengthySubjects } from "../../utils/functions";
import {
  ProgrammingCategories,
  TriviaCategories
} from "../../constants/questionCategories";

const SubjectsContainer = styled.div`
  flex: 1;
  display: flex;
  padding: 3rem;
  flex-wrap: wrap;
`;

const StyledSubject = styled.h3`
  padding: 1rem;
`;

const Subject: React.FC<{ title: string }> = ({ title }) => {
  return <StyledSubject>{title}</StyledSubject>;
};

interface Props {
  quizType: QuizType;
}

export const FourthStep: React.FC<Props & Step> = ({ quizType, goForward }) => {
  const availableSubjects =
    quizType === QuizType.TRIVIA ? TriviaCategories : ProgrammingCategories;
  return (
    <>
      <MainTitle>4. Choose a subject</MainTitle>
      <SubjectsContainer>
        {availableSubjects.map(subject => (
          <Subject key={subject} title={shortenLengthySubjects(subject)!} />
        ))}
      </SubjectsContainer>
    </>
  );
};
