import { TriviaCategory, getCategoryId, Difficulty } from "../models/TriviaApi";
import { TriviaBaseUrl } from "../constants/urls";

export const getQuizApiQuestions = async () => {};

export const getTriviaApiQuestions = async (
  questionsAmount: number,
  category: TriviaCategory,
  difficulty: Difficulty
) => {
  const endpoint = `${TriviaBaseUrl}?amount=${questionsAmount}&category=${getCategoryId(
    category
  )}&difficulty=${difficulty}&type=multiple`;
  const questions = await (await fetch(endpoint)).json();

  return questions;
};
