import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { AuthContext } from "../../../contexts/auth/Auth";
import { useHistory } from "react-router-dom";
import { MediumButton } from "../../UI/Buttons";
import { QuizCreationForm } from "./QuizCreationForm";
import { QuizOverview } from "./QuizOverview";
import { PrivateQuiz } from "../../../models/PrivateQuiz";
import { getUserQuizzes } from "../../../utils/dbFunctions";

const StyledDashboard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

type SectionsToVisit = "quizCreation" | "quizOverview" | "home";

export const UserDashboard: React.FC = () => {
  const { currentUser } = useContext(AuthContext);
  console.log("current user", currentUser);
  const history = useHistory();
  const [sectionVisiting, setSectionVisiting] = useState<SectionsToVisit>(
    "home"
  );
  // const [myQuizzes, setMyQuizzes] = useState<PrivateQuiz[]>([]);

  // console.log("MY Qs", myQuizzes);

  // const getMyQuizzes = async () => {
  //   const quizzes = await getUserQuizzes(currentUser!._id);
  //   setMyQuizzes(quizzes);
  // };

  useEffect(() => {
    history.push("/myDashboard");
  }, [history]);

  // useEffect(() => {
  //   if (currentUser) {
  //     getMyQuizzes();
  //   }
  // }, []);

  const changeSection = (section: SectionsToVisit) =>
    setSectionVisiting(section);

  if (!currentUser) return null;

  return (
    <StyledDashboard>
      {sectionVisiting === "home" ? (
        <>
          <h1>Welcome {currentUser!.username.toUpperCase()}</h1>
          <MediumButton onClick={() => changeSection("quizCreation")}>
            Create new quiz
          </MediumButton>
          <MediumButton onClick={() => changeSection("quizOverview")}>
            See your quizzes
          </MediumButton>
        </>
      ) : sectionVisiting === "quizCreation" ? (
        <QuizCreationForm />
      ) : (
        <QuizOverview myQuizzes={[]} />
      )}
    </StyledDashboard>
  );
};
