import React from "react";
import styled from "styled-components";
import { QuizChoiceButton } from "./QuizChoiceButton";
import { QuizType } from "../models/Question";
import { Button } from "react-native";

const ButtonContainer = styled.div`
  width: 60vw;
  display: flex;
  justify-content: space-between;
`;

interface Props {
  chooseQuiz: (quizType: QuizType) => void;
}

export const FirstStep: React.FC<Props> = ({ chooseQuiz }) => {
  return (
    <>
      <h1>Choose your quiz</h1>
      <ButtonContainer>
        <QuizChoiceButton
          text="TRIVIA"
          callback={() => chooseQuiz(QuizType.TRIVIA)}
        />
        <QuizChoiceButton
          text="PROGRAMMING"
          callback={() => chooseQuiz(QuizType.PROGRAMMING)}
        />
      </ButtonContainer>
    </>
  );
};
