  // const [currentSubject, setCurrentSubject] = useState<Subject | "">("");
  // const [currentStep, setCurrentStep] = useState(1);
  // const [startedQuiz, setStartedQuiz] = useState(false);

  const {
    quizType,
    currentSubject,
    numberOfQuestions,
    difficultyLevel,
    configQuiz,
    quizConfigurationStep,
    startedQuiz,
    uploadQuestions,
    goToNextQuizConfiguration,
    questions,
    reset
  } = useContext(QuizzesContext);

  // const reset = () => {
  //   setStartedQuiz(false);
  //   setCurrentStep(1);
  //   setNumOfQuestions(5);
  //   setDifficultyLevel("medium");
  // };

  // const chooseQuiz = (quizType: QuizType) => {
  //   reset();
  //   setQuizType(quizType);
  // };

  // const chooseAmountOfQuestions = (num: number) => {
  //   setNumOfQuestions(num);
  // };

  // const changeLevel = (level: Difficulty) => setDifficultyLevel(level);

  // const chooseSubject = (subject: Subject) => {
  //   setCurrentSubject(subject);
  // };

  // const goForward = () => setCurrentStep(prev => prev + 1);

  // const uploadQuestions = (questions: Question[]) => {
  //   setQuestions(questions);
  //   setStartedQuiz(true);
  // };

  console.log("QST", questions);

  const renderInstruction = () => {
    switch (quizConfigurationStep) {
      case 1:
        return (
          <SubjectsStep
            quizType={quizType}
            goForward={goToNextQuizConfiguration}
            // updateSubject={chooseSubject}
          />
        );

      case 2:
        return <NumQuestionsStep currentAmount={numberOfQuestions} />;

      case 3:
        return <DifficultyStep currentLevel={difficultyLevel} />;

      case 4:
        return <FinalSummaryStep />;

      default:
        return <h1>SOMETHING WENT WRONG...</h1>;
    }
  };

  return (
    <>
      {/* <Navbar selectedQuiz={quizType} chooseQuiz={chooseQuiz} reset={reset} /> */}
      {!startedQuiz ? (
        <header className="App-header">{renderInstruction()}</header>
      ) : (
        <ActualQuiz
          subject={currentSubject as Subject}
          allQuestions={questions}
          reset={reset}
        />
      )}
    </>
  );
};

