import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject, APISendable } from "../../models/Question";
import { Difficulty } from "../../models/TriviaApi";
import { Colors } from "../../constants/colors";
import { Question } from "../../models/Question";
import { getQuestions } from "../../utils/functions";
import { StepTemplate } from "./StepTemplate";
import { StyledButton } from "../QuizChoiceButton";

const StyledFeature = styled.p`
  color: ${Colors.INDIGO};
  font-size: 0.9rem;
  margin: 0.4rem;
`;

const FeaturesContainer = styled.div`
  width: 50vw;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  background-color: white;
  justify-content: center;
  align-items: center;
`;

const StartButton = styled(StyledButton)`
  background-color: ${Colors.DARK_BLUE};
  margin-top: 1.5rem;

  :hover {
    background-color: ${Colors.ORANGE};
  }
`;

interface Props {
  quizType: QuizType;
  numberOfQuestions: number;
  difficulty: Difficulty;
  subject: Subject;
  uploadQuestions: (questions: Question[]) => void;
}

export const FifthStep: React.FC<Props> = ({
  quizType,
  numberOfQuestions,
  difficulty,
  subject,
  uploadQuestions
}) => {
  return (
    <StepTemplate>
      <FeaturesContainer>
        <StyledFeature>Quiz: {quizType}</StyledFeature>
        <StyledFeature>No. of questions: {numberOfQuestions}</StyledFeature>
        <StyledFeature>Difficulty: {difficulty}</StyledFeature>
        <StyledFeature>Subject: {subject}</StyledFeature>
        <StartButton
          onClick={async () => {
            const questionsToUpload = await getQuestions({
              quizType,
              difficulty,
              numOfQuestions: numberOfQuestions,
              subject
            });

            uploadQuestions(questionsToUpload);
          }}
        >
          START QUIZ
        </StartButton>
      </FeaturesContainer>
    </StepTemplate>
  );
};
