import React from "react";
import { StyledAnswer } from "./Styled";
import { Colors } from "../../../../../constants/colors";
import {
  cropInput,
  removeEntryFromArray
} from "../../../../../utils/functions";
import { Icon } from "../../../../UI/Icon";
import { StyledUploadedReplies } from "./Styled";

interface Props {
  label: string;
  value: string;
  isRight: boolean;
}

export const Answer: React.FC<Props> = ({ label, value, isRight }) => {
  return (
    <StyledAnswer>
      <div>
        <Icon
          icon={isRight ? "check-circle" : "times-circle"}
          color={isRight ? Colors.GREEN : Colors.RED}
        />{" "}
        <h5>
          <span style={{ color: Colors.LIGHTEST_GREY, marginRight: 5 }}>
            {label}
          </span>
          {cropInput(value, 50)}
        </h5>
      </div>
      <Icon icon="times-circle" />
    </StyledAnswer>
  );
};

export const UploadedAnswers: React.FC<{
  replies: string[];
  updateAnswersOnDelete: (answersLeft: string[]) => void;
}> = ({ replies, updateAnswersOnDelete }) => {
  return (
    <StyledUploadedReplies>
      {replies.map((reply, index) => (
        <li key={index}>
          <Answer label="A: " isRight={false} value={reply} />
          {/* <span
            onClick={() => {
              const answersLeft = removeEntryFromArray(replies, reply);
              console.log("Answers left", answersLeft);
              updateAnswersOnDelete(answersLeft);
            }}
          >
            <Icon icon="times-circle" />
          </span> */}
        </li>
      ))}
    </StyledUploadedReplies>
  );
};
