import React, { useReducer, useState, useContext } from "react";
import styled from "styled-components";
import { Input } from "./UI/Input";
import {
  AuthReducers,
  InputName as AuthInputName
} from "../reducers/AuthReducers";
import { InputName as QuizInputName } from "../reducers/AuthReducers";
import { loginUser, registerUser } from "../utils/dbFunctions";
import { AuthScope } from "../models/AuthScope";
import { ApiResponse } from "../models/ApiResponse";
import { AuthContext } from "../contexts/auth/Auth";
import { FormContainer, FormTitle, SubmitButton } from "./UI/Form";

const ErrorMessage = styled.p`
  font-size: 0.8rem;
  color: red;
  margin-top: 0;
`;

type PossibleInputName = AuthInputName | QuizInputName;

export type InputConfig = {
  name: PossibleInputName;
  inputType?: "text" | "password";
};

export interface Action {
  type: PossibleInputName;
  payload?: string;
}

type Props = {
  title: string;
  inputs: InputConfig[];
  reducer: React.Reducer<any, any>;
  inputStartingValues: any;
  handleSubmit: (filledInInputs: any[]) => Promise<void>;
  message: string;
};

export const UniversalForm: React.FC<Props> = ({
  title,
  inputs,
  reducer,
  inputStartingValues,
  handleSubmit,
  message
}) => {
  const [error, setError] = useState("");

  const [inputValues, dispatch] = useReducer(reducer, inputStartingValues);

  const handleChange = (input: string, inputName: PossibleInputName) =>
    dispatch({ type: inputName, payload: input });

  // const handleSubmit = async () => {
  //   let response: ApiResponse;
  //   if (scope === "login") {
  //     response = await loginUser(inputValues);
  //     console.log("RESSSS", response);
  //     loadCurrentUser(response.payload);
  //   } else {
  //     response = await registerUser(inputValues);
  //     console.log("RESSSS", response);
  //     loadCurrentUser(response.payload);
  //   }
  //   if (response.message) {
  //     setError(response.message);
  //   }
  // };

  const resetError = () => setError("");

  return (
    <FormContainer>
      <FormTitle>{title}</FormTitle>
      <form
        onSubmit={e => {
          e.preventDefault();
          handleSubmit(inputValues);
        }}
      >
        {inputs.map(input => (
          <Input
            key={input.name}
            inputType={input.inputType ? input.inputType : "text"}
            inputName={input.name}
            onChangeFunction={handleChange}
            resetError={resetError}
          />
        ))}
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <SubmitButton type="submit">SUBMIT</SubmitButton>
      </form>
    </FormContainer>
  );
};
