import React from "react";
import styled from "styled-components";
import { breakpoints } from "../../constants/breakpoints";
import { Form } from "../UI/Form";

const StyledLogin = styled.div`
  flex: 1;
  display: flex;
  height: 80vh;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  form {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 300px;
    margin-top: 1.5em;
  }

  input {
    padding: 1em;
    width: 100%;
    box-sizing: border-box;
    margin: 1rem 0;
  }

  button {
    background-color: #023e8a;
    padding: 1em;
    width: 100%;
    cursor: pointer;
    color: white;
    font-weight: bold;
  }

  .error {
    color: red;
    padding: 1em;
  }

  @media (max-width: ${breakpoints.smallScreens}px) {
    input {
      height: 50px;
    }
  }
`;

const startingInputValues = [
  { label: "user name", value: "" },
  { label: "password", value: "" }
];

export const Login: React.FC = () => {
  return (
    <StyledLogin>
      <Form
        title="Log in"
        inputs={startingInputValues}
        onSubmitFunction={() => {}}
      />
    </StyledLogin>
  );
};
