import React, { useState } from "react";
import styled from "styled-components";

interface ReplyProps {
  isRight: boolean;
  hasReplied: boolean;
  reply: string;
  replyQuestion: () => void;
}

type PartialProps = Omit<ReplyProps, "reply" | "replyQuestion">;

interface StyledReplyProps extends PartialProps {
  wasSelected: boolean;
}

const StyledReply = styled.p`
  text-align: left;
  line-height: 24px;
  cursor: pointer;
  padding: 1rem;
  background-color: ${(props: StyledReplyProps) =>
    props.hasReplied && props.isRight
      ? "#31ea31"
      : props.wasSelected && !props.isRight
      ? "#ff0f0f"
      : props.isRight && props.hasReplied
      ? "#31ea31"
      : "none"};

  /* :hover {
    background-color: lightgrey;
  } */
`;

export const Reply: React.FC<ReplyProps> = ({ hasReplied, isRight, reply }) => {
  const [wasSelected, setWasSelected] = useState(false);

  return (
    <StyledReply
      hasReplied={hasReplied}
      isRight={isRight}
      wasSelected={wasSelected}
      onClick={() => {
        setWasSelected(true);
      }}
    >
      {reply}
    </StyledReply>
  );
};
