import React from "react";
import { PrivateQuiz, iQuestion } from "../models/PrivateQuiz";

export type InputName =
  | "title"
  | "backgroundImageUrl"
  | "questions"
  | "createdBy"
  | "isPrivate";

export interface QuizCreationAction {
  type: InputName;
  payload?: string | boolean | iQuestion;
}

// QUIZ CREATION
export interface QuizCreationFormInputs extends PrivateQuiz {}

const QuizCreationReducer: React.Reducer<
  QuizCreationFormInputs,
  QuizCreationAction
> = (
  state: QuizCreationFormInputs,
  action: QuizCreationAction
): QuizCreationFormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const QuizCreationStartingValues: QuizCreationFormInputs = {
  title: "",
  backgroundImageUrl: "",
  createdBy: "",
  isPrivate: true,
  questions: []
};

// ALL REDUCERS EXPORT
export const QuizCreation = {
  reducer: QuizCreationReducer,
  startingValues: QuizCreationStartingValues
};
