export interface iQuestion {
  question: string;
  allReplies: string[];
  rightReply: string;
}

export interface PrivateQuiz {
  title: string;
  backgroundImageUrl?: string;
  questions: iQuestion[];
  isPrivate: boolean;
}

export class Question implements iQuestion {
  public allReplies: string[] = [];
  public rightReply: string = "";
  constructor(public question: string) {}

  public addReply(reply: string) {
    this.allReplies.push(reply);
  }

  public insertRightReply(reply: string) {
    this.rightReply = reply;
  }
}

export type PrivacyChoice = "yes" | "no";
