import React from "react";
import { PrivateQuiz } from "../models/PrivateQuiz";

export type InputName = "login" | "password";

export interface AuthAction {
  type: InputName;
  payload?: string;
}

export interface QuizCreationAction {
  type:
    | "title"
    | "backgroundImageUrl"
    | "createdBy"
    | "isPrivate"
    | "questions";
  payload?: string | boolean;
}

// LOGIN
export interface LoginFormInputs {
  username: string;
  password: string;
}

export interface RegistrationFormInputs {
  username: string;
  password: string;
}

const LoginReducer: React.Reducer<LoginFormInputs, AuthAction> = (
  state: LoginFormInputs,
  action: AuthAction
): LoginFormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const LoginStartingValues: LoginFormInputs = {
  username: "",
  password: ""
};

// REGISTRATION
export interface RegistrationFormInputs {
  username: string;
  password: string;
}

const RegisterReducer: React.Reducer<RegistrationFormInputs, AuthAction> = (
  state: RegistrationFormInputs,
  action: AuthAction
): RegistrationFormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const RegisterStartingValues: RegistrationFormInputs = {
  username: "",
  password: ""
};

// QUIZ CREATION
export interface QuizCreationFormInputs extends PrivateQuiz {}

const QuizCreationReducer: React.Reducer<
  QuizCreationFormInputs,
  QuizCreationAction
> = (
  state: QuizCreationFormInputs,
  action: QuizCreationAction
): QuizCreationFormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const QuizCreationStartingValues: QuizCreationFormInputs = {
  title: "",
  backgroundImageUrl: "",
  createdBy: "",
  isPrivate: true,
  questions: []
};

// ALL REDUCERS EXPORT
export const FormReducers = {
  login: {
    reducer: LoginReducer,
    startingValues: LoginStartingValues
  },
  register: {
    reducer: RegisterReducer,
    startingValues: RegisterStartingValues
  },
  quizCreation: {
    reducer: QuizCreationReducer,
    startingValues: QuizCreationStartingValues
  }
};
