import React, { useState } from "react";
import styled from "styled-components";
import { Question } from "../../models/Question";
import { StyledButton } from "../QuizChoiceButton";
import { Colors } from "../../constants/colors";

const StyledCard = styled.div`
  width: 600px;
  padding: 2rem;
  background-color: white;
  align-items: flex-start;
  position: relative;
  border-radius: 20px;
`;

const NextButton = styled(StyledButton)`
  background-color: ${Colors.VIOLET};
  background-color: ${(props: { hasReplied: boolean }) =>
    props.hasReplied ? Colors.VIOLET : "lightgrey"};
`;

const RepliesContainer = styled.div`
  margin: 1rem 0;
  display: flex;
  flex-direction: column;
`;

const QuestionNumber = styled.h4`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #cccccc;
  color: #696969;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Reply = styled.p`
  text-align: left;
  line-height: 24px;
  cursor: pointer;
  padding: 1rem;
  background-color: ${(props: { isRight: boolean; hasReplied: boolean }) =>
    props.hasReplied && props.isRight
      ? "#31ea31"
      : props.hasReplied
      ? "#ff0f0f"
      : "none"};

  /* :hover {
    background-color: lightgrey;
  } */
`;

interface QuestionCardProps {
  question: Question;
  numberOfQuestions: number;
  currentNumberOfQuestion: number;
  next: () => void;
}

export const QuestionCard: React.FC<QuestionCardProps> = ({
  question,
  next,
  numberOfQuestions,
  currentNumberOfQuestion
}) => {
  console.log("QUESTION", question);
  const [hasReplied, setHasReplied] = useState(false);
  const [wasSelected, setWasSelected] = useState(false);

  const isRightReply = (reply: string) => reply === question.rightReply;

  return (
    <StyledCard>
      <h4>{question.question}</h4>
      <RepliesContainer>
        {question.allReplies
          .filter(reply => reply !== null)
          .map((reply, index) => (
            <Reply
              key={index}
              isRight={isRightReply(reply)}
              hasReplied={hasReplied}
              onClick={() => setHasReplied(true)}
            >
              {`${index + 1}. ${reply}`}
            </Reply>
          ))}
      </RepliesContainer>
      <NextButton hasReplied={hasReplied} disabled={!hasReplied} onClick={next}>
        NEXT
      </NextButton>
      <QuestionNumber>{`${currentNumberOfQuestion}/${numberOfQuestions}`}</QuestionNumber>
    </StyledCard>
  );
};
