import React, { useState } from "react";
import "./App.css";
import { getTriviaApiQuestions } from "./utils/triviaAPI";
import { getProgrammingQuizApiQuestions } from "./utils/programmingQuizAPI";
import { QuizType, Question } from "./models/Question";
import { normalizeQuestions } from "./utils/functions";
import { QuizChoiceButton } from "./components/QuizChoiceButton";
import { FirstStep } from "./components/steps/FirstStep";
import { SecondStep } from "./components/steps/SecondStep";
import { ThirdStep } from "./components/steps/ThirdStep";
import { Difficulty } from "./models/TriviaApi";

const App: React.FC = () => {
  const [questions, setQuestions] = useState<Question[]>([]);
  const [quizType, setQuizType] = useState<QuizType>(QuizType.TRIVIA);
  const [numOfQuestions, setNumOfQuestions] = useState<number>(5);
  const [difficultyLevel, setDifficultyLevel] = useState<Difficulty>("medium");
  const [currentStep, setCurrentStep] = useState(1);

  const chooseQuiz = (quizType: QuizType) => {
    setQuizType(quizType);
  };

  const chooseAmountOfQuestions = (num: number) => {
    setNumOfQuestions(num);
  };

  const changeLevel = (level: Difficulty) => setDifficultyLevel(level);

  const goForward = () => setCurrentStep(prev => prev + 1);

  const getQuestions = async () => {
    let questions;
    if (quizType === QuizType.TRIVIA) {
      questions = await getTriviaApiQuestions(
        numOfQuestions,
        "Entertainment: Books",
        "hard"
      );
    } else {
      questions = await getProgrammingQuizApiQuestions(
        numOfQuestions,
        "code",
        "hard"
      );
    }

    const formattedQuestions = normalizeQuestions(quizType, questions);
    setQuestions(formattedQuestions);
  };

  console.log("QST", questions);

  const renderContent = () => {
    switch (currentStep) {
      case 1:
        return <FirstStep chooseQuiz={chooseQuiz} goForward={goForward} />;

      case 2:
        return (
          <SecondStep
            chooseAmountOfQuestions={chooseAmountOfQuestions}
            currentAmount={numOfQuestions}
            goForward={goForward}
          />
        );

      case 3:
        return (
          <ThirdStep
            currentLevel={difficultyLevel}
            changeLevel={changeLevel}
            goForward={goForward}
          />
        );

      default:
        return <h1>NOTHING</h1>;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        {renderContent()}
        {/* <button onClick={getQuestions}>GET QUESTIONS</button> */}
      </header>
    </div>
  );
};

export default App;
