import React from "react";
import styled from "styled-components";
import { Colors } from "../../constants/colors";
import { Input } from "./Input";

const FormContainer = styled.div`
  width: 600px;
  height: 300px;
  background-color: ${Colors.YELLOW};
  color: ${Colors.BLACK};
  display: flex;
  flex-direction: column;
  align-items: center;
`;

// const Input = styled.input`
//   padding: 1rem;
//   background-color: white;
// `;

type InputConfig = {
  name: string;
};

type Props = {
  title: string;
  inputs: InputConfig[];
  onSubmitFunction: any;
};

export const Form: React.FC<Props> = ({ title, inputs, onSubmitFunction }) => {
  return (
    <FormContainer>
      <h3>{title}</h3>
      <form onSubmit={onSubmitFunction}>
        {inputs.map(input => (
          <Input key={input.name} name={input.name} />
          // <div key={input.label}>
          //   <label htmlFor={input.label}>{input.label.toUpperCase()}</label>
          //   <Input type="text" name={input.label} />
          // </div>
        ))}
      </form>
    </FormContainer>
  );
};
