import React, { useState, useEffect } from "react";
import styled from "styled-components";

interface ReplyProps {
  isRight: boolean;
  hasReplied: boolean;
  replyText: string;
  replyQuestion: () => void;
  givePoint: () => void;
}

type PartialProps = Omit<
  ReplyProps,
  "replyText" | "replyQuestion" | "givePoint"
>;

interface StyledReplyProps extends PartialProps {
  wasSelected: boolean;
}

const StyledReply = styled.p`
  text-align: left;
  line-height: 24px;
  cursor: pointer;
  padding: 0.6rem;
  background-color: ${(props: StyledReplyProps) =>
    props.hasReplied && props.isRight
      ? "#31ea31"
      : props.hasReplied && props.wasSelected && !props.isRight
      ? "#ff0f0f"
      : "none"};

  :hover {
    background-color: ${(props: StyledReplyProps) =>
      !props.hasReplied ? "lightgrey" : "none"};
  }
`;

const ReplyNumber = styled.span`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 2px solid;
`;

export const Reply: React.FC<ReplyProps> = ({
  hasReplied,
  isRight,
  replyText,
  replyQuestion,
  givePoint
}) => {
  const [wasSelected, setWasSelected] = useState(false);

  useEffect(() => {
    if (!hasReplied) {
      setWasSelected(false);
    }
  }, [hasReplied]);

  return (
    <>
      <StyledReply
        hasReplied={hasReplied}
        isRight={isRight}
        wasSelected={wasSelected}
        onClick={() => {
          setWasSelected(true);
          replyQuestion();
          if (isRight) {
            givePoint();
          }
        }}
        dangerouslySetInnerHTML={{ __html: replyText }}
      />
    </>
  );
};
