import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject } from "../../models/Question";
import { shortenLengthySubjects } from "../../utils/functions";
import {
  ProgrammingCategories,
  TriviaCategories
} from "../../constants/questionCategories";
import { Colors } from "../../constants/colors";
import { StepTemplate } from "./StepTemplate";

const SubjectsContainer = styled.div`
  flex: 1;
  display: flex;
  padding: 3rem;
  flex-wrap: wrap;
  justify-content: center;
`;

const StyledSubject = styled.p`
  padding: 1rem;
  cursor: pointer;
  padding: 0.5rem 1rem;
  border-radius: 40px;
  border: 2px solid white;
  text-transform: uppercase;
  background-color: ${Colors.PURPLE_X_11_2};
  font-size: 0.9rem;
  margin: 0.5rem;

  :hover {
    background-color: ${Colors.VIOLET};
  }
`;

const SubjectElement: React.FC<{ title: string; value: Subject } & Updatable &
  Step> = ({ title, value, updateSubject, goForward }) => {
  return (
    <StyledSubject
      onClick={() => {
        updateSubject(value);
        goForward();
      }}
    >
      {title}
    </StyledSubject>
  );
};

interface Props {
  quizType: QuizType;
}

interface Updatable {
  updateSubject: (subject: Subject) => void;
}

export const FourthStep: React.FC<Props & Step & Updatable> = ({
  quizType,
  updateSubject,
  goForward
}) => {
  const availableSubjects =
    quizType === QuizType.TRIVIA ? TriviaCategories : ProgrammingCategories;
  return (
    <StepTemplate>
      <MainTitle>4. Choose a subject</MainTitle>
      <SubjectsContainer>
        {availableSubjects.map(subject => (
          <SubjectElement
            key={subject}
            value={subject as Subject}
            title={shortenLengthySubjects(subject as string)!}
            updateSubject={updateSubject}
            goForward={goForward}
          />
        ))}
      </SubjectsContainer>
    </StepTemplate>
  );
};
