import styled from "styled-components";
import { Colors } from "../../constants/colors";

export const Modal = styled.div`
  position: fixed;
  top: 0;
  width: 100vw;
  height: 100vh;
  background-color: ${Colors.DARK_BLUE};
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 3;
`;
