import React, { useReducer } from "react";
import styled from "styled-components";
import { Colors } from "../../constants/colors";
import { Input } from "./Input";
import { FormReducer, InputName } from "../../reducers/FormReducers";

const FormContainer = styled.div`
  width: 600px;
  height: 300px;
  background-color: ${Colors.YELLOW};
  color: ${Colors.BLACK};
  display: flex;
  flex-direction: column;
  align-items: center;
`;

type InputConfig = {
  name: InputName;
};

type Props = {
  title: string;
  inputs: InputConfig[];
  onSubmitFunction: any;
  scope: "register" | "login";
};

export const Form: React.FC<Props> = ({
  title,
  inputs,
  onSubmitFunction,
  scope
}) => {
  const [inputValues, dispatch] = useReducer(FormReducer, {
    username: "",
    password: ""
  });

  console.log("INPUTS", inputValues);

  const handleChange = (input: string, inputName: InputName) =>
    dispatch({ type: inputName, payload: input });

  return (
    <FormContainer>
      <h3>{title}</h3>
      <form onSubmit={onSubmitFunction}>
        {inputs.map(input => (
          <Input
            key={input.name}
            inputName={input.name}
            onChangeFunction={handleChange}
          />
          // <div key={input.label}>
          //   <label htmlFor={input.label}>{input.label.toUpperCase()}</label>
          //   <Input type="text" name={input.label} />
          // </div>
        ))}
      </form>
    </FormContainer>
  );
};
