import styled from "styled-components";
import { Colors } from "../../constants/colors";

export const MainTitle = styled.h1`
  color: ${Colors.BLACK};
  padding-bottom: 0.5rem;
  /* border-bottom: 5px solid ${Colors.YELLOW}; */
  margin-bottom: 4rem;
  position: relative;

  &::after {
    content: "";
    position: absolute;
    bottom: -8px;
    content: "";
    width: 300px;
    height: 5px;
    left: -50%;
    background-color: ${Colors.YELLOW};
  }
`;
