import React from "react";

export type InputName = "login" | "password";

export interface Action {
  type: InputName;
  payload?: string;
}

interface FormInputs {
  username: string;
  password: string;
}

export const FormReducer: React.Reducer<FormInputs, Action> = (
  state: FormInputs,
  action: Action
): FormInputs => {
  return {
    ...state,
    [action.type]: action.payload
  };
};
