import React, { useState } from "react";
import "./App.css";
import { TriviaApiResponse } from "./models/TriviaApi";
import { getTriviaApiQuestions } from "./utils/triviaAPI";
import { getProgrammingQuizApiQuestions } from "./utils/programmingQuizAPI";
import { QuizType } from "./models/Question";
import { normalizeQuestions } from "./utils/functions";

const App: React.FC = () => {
  const [questions, setQuestions] = useState<TriviaApiResponse>([]);
  const [quizType, setQuizType] = useState<QuizType>("trivia");

  const getQuestions = async () => {
    let questions;
    if (quizType === "trivia") {
      questions = await getTriviaApiQuestions(
        5,
        "Entertainment: Books",
        "hard"
      );
    } else {
      questions = await getProgrammingQuizApiQuestions(5, "code", "Hard");
    }

    setQuestions(questions);
  };

  console.log("QST", questions);

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={() => setQuizType("trivia")}>TRIVIA</button>
        <button onClick={() => setQuizType("programming")}>PROGRAMMING</button>

        <p>
          Edit <code>src/App.tsx</code> and save to reload.
        </p>
        <button onClick={getQuestions}>GET QUESTIONS</button>
      </header>
    </div>
  );
};

export default App;
