import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject } from "../../models/Question";
import { shortenLengthySubjects } from "../../utils/functions";
import {
  ProgrammingCategories,
  TriviaCategories
} from "../../constants/questionCategories";
import { Colors } from "../../constants/colors";

const SubjectsContainer = styled.div`
  flex: 1;
  display: flex;
  padding: 3rem;
  flex-wrap: wrap;
  background-color: ${Colors.PURPLE_X_11_2};
`;

const StyledSubject = styled.h3`
  padding: 1rem;
  cursor: pointer;
`;

const SubjectElement: React.FC<{ title: string } & Updatable & Step> = ({
  title,
  updateSubject,
  goForward
}) => {
  return (
    <StyledSubject
      onClick={() => {
        updateSubject(title as Subject);
        goForward();
      }}
    >
      {title}
    </StyledSubject>
  );
};

interface Props {
  quizType: QuizType;
}

interface Updatable {
  updateSubject: (subject: Subject) => void;
}

export const FourthStep: React.FC<Props & Step & Updatable> = ({
  quizType,
  updateSubject,
  goForward
}) => {
  const availableSubjects =
    quizType === QuizType.TRIVIA ? TriviaCategories : ProgrammingCategories;
  return (
    <>
      <MainTitle>4. Choose a subject</MainTitle>
      <SubjectsContainer>
        {availableSubjects.map(subject => (
          <SubjectElement
            key={subject}
            title={shortenLengthySubjects(subject as string)!}
            updateSubject={updateSubject}
            goForward={goForward}
          />
        ))}
      </SubjectsContainer>
    </>
  );
};
