import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject, APISendable } from "../../models/Question";
import { Difficulty } from "../../models/TriviaApi";
import { Colors } from "../../constants/colors";
import {} from "../../models/Question";

const StyledFeature = styled.h5`
  color: ${Colors.INDIGO};
`;

const FeaturesContainer = styled.div`
  width: 50vw;
  border-radius: 40px;
  border: 2px solid white;
  padding: 2rem;
  display: flex;
  flex-direction: column;
`;

type GetQuestionsFn = (configs: APISendable) => Question[];

interface Props {
  quizType: QuizType;
  numberOfQuestions: number;
  difficulty: Difficulty;
  subject: Subject;
  getQuestions: GetQuestionsFn;
}

export const FifthStep: React.FC<Props> = ({
  quizType,
  numberOfQuestions,
  difficulty,
  subject,
  getQuestions
}) => {
  return (
    <>
      <MainTitle>Start your quiz</MainTitle>
      <FeaturesContainer>
        <StyledFeature>Quiz: {quizType}</StyledFeature>
        <StyledFeature>No. of questions: {numberOfQuestions}</StyledFeature>
        <StyledFeature>Difficulty: {difficulty}</StyledFeature>
        <StyledFeature>Subject: {subject}</StyledFeature>
      </FeaturesContainer>
    </>
  );
};
