import React, { useReducer } from "react";
import styled from "styled-components";
import { Colors } from "../../constants/colors";
import { Input } from "./Input";
import { FormReducers, InputName } from "../../reducers/FormReducers";
import { StyledButton } from "../QuizChoiceButton";

const FormContainer = styled.div`
  width: 600px;
  height: 300px;
  background-color: ${Colors.YELLOW};
  color: ${Colors.BLACK};
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SubmitButton = styled(StyledButton)`
  width: 60px;
  background-color: red;
  padding: 0 0;
`;

type InputConfig = {
  name: InputName;
};

type Props = {
  title: string;
  inputs: InputConfig[];
  onSubmitFunction: any;
  scope: "register" | "login";
};

export const Form: React.FC<Props> = ({
  title,
  inputs,
  onSubmitFunction,
  scope
}) => {
  const relevantReducer =
    scope === "login"
      ? FormReducers.login.reducer
      : FormReducers.register.reducer;
  const relevantStartingValues =
    scope === "login"
      ? FormReducers.login.startingValues
      : FormReducers.register.startingValues;
  const [inputValues, dispatch] = useReducer(
    relevantReducer,
    relevantStartingValues
  );

  console.log("INPUTS", inputValues);

  const handleChange = (input: string, inputName: InputName) =>
    dispatch({ type: inputName, payload: input });

  return (
    <FormContainer>
      <h3>{title}</h3>
      <form onSubmit={onSubmitFunction}>
        {inputs.map(input => (
          <Input
            key={input.name}
            inputName={input.name}
            onChangeFunction={handleChange}
          />
        ))}
        <SubmitButton>{title}</SubmitButton>
      </form>
    </FormContainer>
  );
};
