import React, { useState } from "react";
import styled from "styled-components";

interface ReplyProps {
  isRight: boolean;
  hasReplied: boolean;
  replyText: string;
  replyQuestion: () => void;
}

type PartialProps = Omit<ReplyProps, "replyText" | "replyQuestion">;

interface StyledReplyProps extends PartialProps {
  wasSelected: boolean;
}

const StyledReply = styled.p`
  text-align: left;
  line-height: 24px;
  cursor: pointer;
  padding: 1rem;
  background-color: ${(props: StyledReplyProps) =>
    props.hasReplied && props.isRight
      ? "#31ea31"
      : props.hasReplied && props.wasSelected && !props.isRight
      ? "#ff0f0f"
      : "none"};

  :hover {
    background-color: ${(props: StyledReplyProps) =>
      !props.hasReplied ? "lightgrey" : "none"};
  }
`;

export const Reply: React.FC<ReplyProps> = ({
  hasReplied,
  isRight,
  replyText,
  replyQuestion
}) => {
  const [wasSelected, setWasSelected] = useState(false);

  useEffect(() => {
    setWasSelected(false);
  }, []);

  return (
    <StyledReply
      hasReplied={hasReplied}
      isRight={isRight}
      wasSelected={wasSelected}
      onClick={() => {
        setWasSelected(true);
        replyQuestion();
      }}
    >
      {replyText}
    </StyledReply>
  );
};
