import {
  ProgrammingQuizCategory,
  Difficulty
} from "../models/ProgrammingQuizApi";
import { ProgrammingQuizBaseUrl } from "../constants/urls";
import { Question } from "../models/Question";

export const getProgrammingQuizApiQuestions = async (
  questionsAmount: number = 5,
  category: ProgrammingQuizCategory,
  difficulty: Difficulty = "Medium"
) => {
  const testQuestion = new Question(
    "do you know?",
    "medium",
    "code",
    ["a", "b", "c"],
    "a"
  );
  const questions = await (
    await fetch(
      `${ProgrammingQuizBaseUrl}&category=${category}&difficulty=${difficulty}&limit=${questionsAmount}`
    )
  ).json();

  return questions;
};
