import React, { useState } from "react";
import styled from "styled-components";
import { Question } from "../../models/Question";
import { StyledButton } from "../QuizChoiceButton";
import { Colors } from "../../constants/colors";
import { Reply } from "./Reply";

const StyledCard = styled.div`
  width: 600px;
  padding: 2rem;
  background-color: white;
  align-items: flex-start;
  position: relative;
  border-radius: 20px;
  border: 3px solid;
`;

const NextButton = styled(StyledButton)`
  background-color: ${Colors.VIOLET};
  background-color: ${(props: { hasReplied: boolean }) =>
    props.hasReplied ? Colors.VIOLET : Colors.LIGHTER_GREY};
  cursor: ${(props: { hasReplied: boolean }) =>
    props.hasReplied ? "pointer" : "auto"};

  :hover {
    background-color: ${(props: { hasReplied: boolean }) =>
      props.hasReplied ? Colors.VIOLET : Colors.LIGHTER_GREY};
  }
`;

const RepliesContainer = styled.div`
  margin: 1rem 0;
  display: flex;
  flex-direction: column;
`;

const QuestionNumber = styled.h4`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #cccccc;
  color: #696969;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface QuestionCardProps {
  question: Question;
  numberOfQuestions: number;
  currentNumberOfQuestion: number;
  next: () => void;
  givePoint: () => void;
}

export const QuestionCard: React.FC<QuestionCardProps> = ({
  question,
  next,
  numberOfQuestions,
  currentNumberOfQuestion,
  givePoint
}) => {
  console.log("QUESTION", question);
  const [hasReplied, setHasReplied] = useState(false);

  const replyQuestion = () => setHasReplied(true);
  const isRightReply = (reply: string) => reply === question.rightReply;

  const goToNextQuestion = () => {
    setHasReplied(false);
    next();
  };

  return (
    <StyledCard>
      <h4 dangerouslySetInnerHTML={{ __html: question.question }} />
      <RepliesContainer>
        {question.allReplies
          .filter(reply => reply !== null)
          .map((reply, index) => (
            <Reply
              key={index}
              isRight={isRightReply(reply)}
              hasReplied={hasReplied}
              replyText={reply}
              replyQuestion={replyQuestion}
              givePoint={givePoint}
            />
          ))}
      </RepliesContainer>
      <NextButton
        hasReplied={hasReplied}
        disabled={!hasReplied}
        onClick={goToNextQuestion}
      >
        NEXT
      </NextButton>
      <QuestionNumber>{`${currentNumberOfQuestion}/${numberOfQuestions}`}</QuestionNumber>
    </StyledCard>
  );
};
