import React, { useState } from "react";
import styled from "styled-components";
import { Question } from "../../../models/PrivateQuiz";
import { FormContainer, FormTitle, SubmitButton } from "../../UI/Form";
import {
  Input,
  StyledInput,
  Container as InputContainer,
  StyledLabel
} from "../../UI/Input";
import { MediumButton } from "../../UI/Buttons";

const QuestionInput: React.FC<{
  label: string;
  handleChangeFunction: any;
  value: string;
}> = ({ label, handleChangeFunction }) => {
  return (
    <InputContainer>
      <StyledLabel>{label}</StyledLabel>
      <StyledInput type="text" onChange={handleChangeFunction} value={value} />
    </InputContainer>
  );
};

const Modal = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  background-color: red;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface Props {
  saveQuestionInState: (question: Question) => void;
}

export const QuestionSubForm: React.FC<Props> = ({ saveQuestionInState }) => {
  const [question, setQuestion] = useState("");
  const [currentReply, setCurrentReply] = useState("");
  const [allReplies, setAllReplies] = useState<string[]>([]);
  const [rightReply, setRightReply] = useState("");

  console.log(question);
  console.log(currentReply);
  console.log(allReplies);

  return (
    <Modal>
      <FormContainer>
        <FormTitle>Add a question to your quiz</FormTitle>
        <form
          onSubmit={(e: React.FormEvent<HTMLFormElement>) => {
            e.preventDefault();
            // saveQuestionInState();
          }}
        >
          <QuestionInput
            label="Question"
            value={question}
            handleChangeFunction={(e: React.ChangeEvent<HTMLFormElement>) =>
              setQuestion(e.target.value)
            }
          />

          {allReplies.length > 0 && (
            <ul>
              {allReplies.map((reply, index) => (
                <li key={index}>{reply}</li>
              ))}
            </ul>
          )}

          <div style={{ display: "flex" }}>
            <QuestionInput
              value={currentReply}
              label="Reply"
              handleChangeFunction={(e: React.ChangeEvent<HTMLFormElement>) =>
                setCurrentReply(e.target.value)
              }
            />

            <MediumButton
              onClick={() => {
                setAllReplies(prevReplies => [...prevReplies, currentReply]);
                setCurrentReply("");
              }}
            >
              ADD
            </MediumButton>
          </div>
        </form>
      </FormContainer>
    </Modal>
  );
};
