import React from "react";
import styled from "styled-components";
import { Logo } from "./Logo";
import { Colors } from "../constants/colors";
import { QuizType } from "../models/Question";

const NavbarContainer = styled.div`
  max-width: 100vw;
  display: flex;
  height: 70px;
  -webkit-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  -moz-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  padding: 0 2.5rem 0 1.5rem;
  align-items: center;
  justify-content: space-between;
`;

const MenuItems = styled.ul`
  list-style: none;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 300px;
  font-family: "Fredoka One", cursive;
  color: ${Colors.LIGHTER_GREY};
  cursor: pointer;
  font-size: 0.9rem;
`;

const MenuItem = styled.li`
  border-bottom: ${(props: { value: QuizType; selectedNow: QuizType }) =>
    props.selectedNow === props.value
      ? "4px solid #ffcf10"
      : "4px solid white"};

  :hover {
    border-bottom: 4px solid ${Colors.YELLOW};
  }
`;

interface NavbarProps {
  selectedQuiz: QuizType;
}

export const Navbar: React.FC<NavbarProps> = ({ selectedQuiz }) => {
  return (
    <NavbarContainer>
      <Logo />
      <MenuItems>
        <MenuItem value={QuizType.TRIVIA}>TRIVIA QUIZ</MenuItem>
        <MenuItem value={QuizType.PROGRAMMING}>PROGRAMMING QUIZ</MenuItem>
      </MenuItems>
    </NavbarContainer>
  );
};
