import React from "react";
import styled from "styled-components";
import { Clickable } from "./Clickable";

export const StyledQuizBackground = styled.div`
  width: 100vw;
  min-height: 100vh;
  position: fixed;
  top: 0;
  background-image: ${(props: { imageUrl: string }) =>
    `url("${props.imageUrl}")`};
  background-position: center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const StopQuizButton = styled(Clickable)`
  position: fixed;
  top: 40px;
  right: 40px;
`;

interface Props {
  imageUrl: string;
}

export const QuizBackground: React.FC<Props> = ({ children, imageUrl }) => {
  return (
    <StyledQuizBackground imageUrl={imageUrl}>{children}</StyledQuizBackground>
  );
};
