import React, { useState } from "react";
import styled from "styled-components";
import { Question } from "../../../models/PrivateQuiz";
import { FormContainer, FormTitle, SubmitButton } from "../../UI/Form";
import {
  Input,
  StyledInput,
  Container as InputContainer,
  StyledLabel
} from "../../UI/Input";

const Modal = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  background-color: red;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface Props {
  saveQuestionInState: (question: Question) => void;
}

export const QuestionSubForm: React.FC<Props> = ({ saveQuestionInState }) => {
  const handleChange = () => {};
  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [allReplies, setAllReplies] = useState<string[]>([]);
  const [rightReply, setRightReply] = useState("");

  return (
    <Modal>
      <FormContainer>
        <FormTitle>Add a question to your quiz</FormTitle>
        <form
          onSubmit={(e: React.FormEvent<HTMLFormElement>) => {
            e.preventDefault();
            // saveQuestionInState();
          }}
        >
          <InputContainer>
            <StyledLabel>Question</StyledLabel>
            <StyledInput type="text" onChange={handleChange} />
          </InputContainer>
          {/* <Input inputName="title" onChangeFunction={handleChange} /> */}
        </form>
      </FormContainer>
    </Modal>
  );
};
