import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { AuthContext } from "../../../contexts/auth/Auth";
import { useHistory } from "react-router-dom";
import { MediumButton } from "../../UI/Buttons";
import { QuizCreationForm } from "./QuizCreationForm";

const StyledDashboard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const UserDashboard: React.FC = () => {
  const { currentUser } = useContext(AuthContext);
  const history = useHistory();
  const [sectionVisiting, setSectionVisiting] = useState<
    "quizCreation" | "quizOverview" | null
  >(null);

  useEffect(() => {
    history.push("/myDashboard");
  }, [history]);

  if (!currentUser) return null;

  return (
    <StyledDashboard>
      <h1>Welcome {currentUser!.username.toUpperCase()}</h1>
      <MediumButton>Create new quiz</MediumButton>
      <MediumButton>See your quizzes</MediumButton>
      <QuizCreationForm />
    </StyledDashboard>
  );
};
