import React from "react";
import styled from "styled-components";
import { Question } from "../../../models/PrivateQuiz";
import { FormContainer, FormTitle, SubmitButton } from "../../UI/Form";
import { Input } from "../../UI/Input";

const Modal = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  background-color: red;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface Props {
  saveQuestionInState: (question: Question) => void;
}

export const QuestionSubForm: React.FC<Props> = ({ saveQuestionInState }) => {
  const handleChange = () => {};
  const handleSubmit = () => {};

  return (
    <Modal>
      <FormContainer>
        <FormTitle>Add a question to your quiz</FormTitle>
        <form
          onSubmit={(e: React.FormEvent<HTMLFormElement>) => {
            e.preventDefault();
            handleSubmit();
          }}
        >
          <input type="text" />
          {/* <Input inputName="title" onChangeFunction={handleChange} /> */}
        </form>
        <h2>QUESTIOn</h2>
      </FormContainer>
    </Modal>
  );
};
