import React from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject, APISendable } from "../../models/Question";
import { Difficulty } from "../../models/TriviaApi";
import { Colors } from "../../constants/colors";
import { Question } from "../../models/Question";
import { getQuestions } from "../../utils/functions";
import { StepTemplate } from "./StepTemplate";
import { StyledButton } from "../QuizChoiceButton";

const StyledFeature = styled.p`
  color: ${Colors.LIGHTER_GREY};
  font-size: 1.2rem;
  margin: 0.4rem;

  span {
    color: ${Colors.BLACK};
    text-transform: uppercase;
    font-family: "Fredoka One", cursive;
  }
`;

const FeaturesContainer = styled.div`
  width: 50vw;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  background-color: white;
  justify-content: center;
  align-items: center;
`;

const StartButton = styled(StyledButton)`
  background-color: ${Colors.DARK_BLUE};
  margin-top: 1.5rem;

  :hover {
    background-color: ${Colors.ORANGE};
  }
`;

interface Props {
  quizType: QuizType;
  numberOfQuestions: number;
  difficulty: Difficulty;
  subject: Subject;
  uploadQuestions: (questions: Question[]) => void;
}

export const FinalSummaryStep: React.FC<Props> = ({
  quizType,
  numberOfQuestions,
  difficulty,
  subject,
  uploadQuestions
}) => {
  return (
    <StepTemplate>
      <FeaturesContainer>
        <StyledFeature>
          Quiz: <span>{quizType}</span>
        </StyledFeature>
        <StyledFeature>
          No. of questions: <span>{numberOfQuestions}</span>
        </StyledFeature>
        <StyledFeature>
          Difficulty: <span>{difficulty}</span>
        </StyledFeature>
        <StyledFeature>
          Subject: <span>{subject}</span>
        </StyledFeature>
        <StartButton
          onClick={async () => {
            const questionsToUpload = await getQuestions({
              quizType,
              difficulty,
              numOfQuestions: numberOfQuestions,
              subject
            });

            uploadQuestions(questionsToUpload);
          }}
        >
          START QUIZ
        </StartButton>
      </FeaturesContainer>
    </StepTemplate>
  );
};
