import React from "react";
import styled from "styled-components";
import { Logo } from "./Logo";
import { Colors } from "../constants/colors";

const NavbarContainer = styled.div`
  max-width: 100vw;
  display: flex;
  height: 70px;
  -webkit-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  -moz-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  padding: 0 2.5rem 0 1.5rem;
  align-items: center;
  justify-content: space-between;
`;

const MenuItems = styled.ul`
  list-style: none;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 300px;
  font-family: "Fredoka One", cursive;
  color: ${Colors.LIGHTER_GREY};
  cursor: pointer;
  font-size: 0.9rem;
  border-bottom: ${(props: { value: string; selectedNow: string }) =>
      props.selectedNow === props.value
        ? "4px solid #ffcf10"
        : "4px solid white"}
    li {
    :hover {
      border-bottom: 4px solid ${Colors.YELLOW};
    }
  }
`;

export const Navbar: React.FC = () => {
  return (
    <NavbarContainer>
      <Logo />
      <MenuItems>
        <li>TRIVIA QUIZ</li>
        <li>PROGRAMMING QUIZ</li>
      </MenuItems>
    </NavbarContainer>
  );
};
