import React, { useState } from "react";
import styled from "styled-components";
import { Question } from "../../../models/PrivateQuiz";
import { FormContainer, FormTitle, SubmitButton } from "../../UI/Form";
import {
  Input,
  StyledInput,
  Container as InputContainer,
  StyledLabel
} from "../../UI/Input";
import { MediumButton } from "../../UI/Buttons";

const QuestionInput: React.FC<{ label: string; handleChangeFunction: any }> = ({
  label,
  handleChangeFunction
}) => {
  return (
    <InputContainer>
      <StyledLabel>{label}</StyledLabel>
      <StyledInput type="text" onChange={handleChangeFunction} />
    </InputContainer>
  );
};

const Modal = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  background-color: red;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface Props {
  saveQuestionInState: (question: Question) => void;
}

export const QuestionSubForm: React.FC<Props> = ({ saveQuestionInState }) => {
  const [question, setQuestion] = useState("");
  const [reply, setReply] = useState("");
  const [allReplies, setAllReplies] = useState<string[]>([]);
  const [rightReply, setRightReply] = useState("");

  return (
    <Modal>
      <FormContainer>
        <FormTitle>Add a question to your quiz</FormTitle>
        <form
          onSubmit={(e: React.FormEvent<HTMLFormElement>) => {
            e.preventDefault();
            // saveQuestionInState();
          }}
        >
          <QuestionInput
            label="Question"
            handleChangeFunction={(e: React.ChangeEvent<HTMLFormElement>) =>
              setQuestion(e.target.value)
            }
          />
          <div style={{ display: "flex" }}>
            <QuestionInput
              label="Reply"
              handleChangeFunction={(e: React.ChangeEvent<HTMLFormElement>) =>
                setReply(e.target.value)
              }
            />
            <MediumButton>ADD</MediumButton>
          </div>
        </form>
      </FormContainer>
    </Modal>
  );
};
