import React from "react";

export type InputName = "login" | "password";

export interface Action {
  type: InputName;
  payload?: string;
}

interface FormInputs {
  username: string;
  password: string;
}

const LoginReducer: React.Reducer<FormInputs, Action> = (
  state: FormInputs,
  action: Action
): FormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const LoginStartingValues = {
  username: "",
  password: ""
};

const RegisterReducer: React.Reducer<FormInputs, Action> = (
  state: FormInputs,
  action: Action
): FormInputs => {
  const newState = {
    ...state,
    [action.type]: action.payload
  };
  return newState;
};

const RegisterStartingValues = {
  username: "",
  password: ""
};

export const FormReducers = {
  login: {
    reducer: LoginReducer,
    startingValues: LoginStartingValues
  },
  register: {
    reducer: RegisterReducer,
    startingValues: RegisterStartingValues
  }
};
