import React, { useState } from "react";
import styled from "styled-components";
import { Question, QuizType } from "../models/Question";
import progBground from "../images/programming.jpg";
import { QuestionCard } from "./replies/QuestionCard";

const Background = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url(${progBground});
  background-position: center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

interface ActualQuizProps {
  allQuestions: Question[];
  quizType: QuizType;
}

export const ActualQuiz: React.FC<ActualQuizProps> = ({
  quizType,
  allQuestions
}) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const goToNextQuestion = () => setCurrentQuestionIndex(prev => prev + 1);

  return (
    <Background>
      <QuestionCard
        question={allQuestions[currentQuestionIndex]}
        next={goToNextQuestion}
        numberOfQuestions={allQuestions.length}
        currentNumberOfQuestion={currentQuestionIndex + 1}
      />
    </Background>
  );
};
