import React from "react";
import styled from "styled-components";
import { QuizType } from "../../models/PublicQuizQuestion";
import { Colors } from "../../constants/colors";
import { LoginButton } from "../UI/Buttons";
import { breakpoints } from "../../constants/breakpoints";
import { Icon } from "../UI/Icon";

const StyledBurgerIcon = styled.div`display: none;
  width: 2rem;
  height: 2rem;
  z-index: 20;

  @media (max-width: ${breakPoints.smallScreens}) {
    display: flex;
    flex-flow: column nowrap;
    justify-content: space-around;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background-color: ${props =>
      props.isOpen ? props.theme.body : props.theme.text};
    /* ${({ isOpen }) => (isOpen ? "grey" : "yellow")}; */
    border-radius: 10px;
    transform-origin: 1px;
    transition: all .2s linear;

    &:nth-child(1) {
      transform: ${({ isOpen }) => (isOpen ? "rotate(45deg)" : "rotate(0)")}
    }

    &:nth-child(2) {
      transform: ${({ isOpen }) =>
        isOpen ? "translateX(100%)" : "translateX(0)"};
      opacity: ${({ isOpen }) => (isOpen ? 0 : 1)};
    }

    &:nth-child(3) {
      transform: ${({ isOpen }) => (isOpen ? "rotate(-45deg)" : "rotate(0)")}
    }
  }`;

const BurgerIcon: React.FC = () => {
  return (
    <StyledBurgerIcon
      isOpen={isMobileMenuOpen}
      onClick={() => {
        isMobileMenuOpen ? closeMobileMenu() : openMobileMenu();
      }}
    >
      <div />
      <div />
      <div />
    </StyledBurgerIcon>
  );
};

export const AllLinkWrapper = styled.div`
  display: flex;
  justify-content: flexend;
  align-items: center;

  @media (max-width: ${breakpoints.smallScreens}) {
    visibility: hidden;
    background-color: red;
    flex-direction: column;
    align-items: flex-end;
    position: fixed;
    top: 0;
    right: 0;
    height: 90vh;
    padding: 3rem 0;
    background-color: ${Colors.INDIGO};
    z-index: 50;
  }
`;

export const MenuItemsContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  flex: ${(props: { flex: number }) => props.flex};

  @media (max-width: ${breakpoints.smallScreens}) {
    flex-direction: column;
    align-items: flex-end;
    height: 200px;
    justify-content: space-around;
  }
`;

export const NavbarMenuItem = styled.li`
  color: ${(props: { value: QuizType; selectedNow: QuizType }) =>
    props.selectedNow === props.value ? Colors.BLACK : Colors.LIGHTER_GREY};
  border-bottom: ${(props: { value: QuizType; selectedNow: QuizType }) =>
    props.selectedNow === props.value
      ? "4px solid #ffcf10"
      : "4px solid white"};

  padding: 0 0.5rem 4px 0.5rem;
  margin: 0 1.4rem;

  :hover {
    border-bottom: ${(props: { value: QuizType; selectedNow: QuizType }) =>
      props.selectedNow === props.value
        ? "4px solid #ffcf10"
        : "4px solid #b9b5b5"};
  }

  @media (max-width: ${breakpoints.smallScreens}) {
    font-size: 1.4rem;
    margin: 0;
    padding: 0;
    color: white;
    color: ${(props: { value: QuizType; selectedNow: QuizType }) =>
      props.selectedNow === props.value ? "white" : Colors.LIGHTER_GREY};
  }
`;

export const NavbarContainer = styled.div`
  max-width: 100vw;
  display: flex;
  height: 70px;
  -webkit-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  -moz-box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  box-shadow: 0px 0px 3px -1px rgba(87, 84, 87, 1);
  padding: 0 2.5rem 0 1.5rem;
  align-items: center;
  justify-content: space-between;
`;

export const MenuItems = styled.ul`
  list-style: none;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
  font-family: "Fredoka One", cursive;
  cursor: pointer;
  font-size: 0.9rem;
  margin: 0 3rem;
`;

export const BackButton = styled(LoginButton)`
  width: 160px;
`;
