import React, { useReducer } from "react";
import styled from "styled-components";
import {
  QuizCreationReducer,
  QuizCreationStartingValues,
  QuizCreationAction,
  InputName
} from "../../../reducers/QuizCreation";
import { Input, StyledLabel } from "../../UI/Input";
import { FormContainer, FormTitle, SubmitButton } from "../../UI/Form";
import { Colors } from "../../../constants/colors";

const Container = styled(FormContainer)`
  height: 500px;
  /* background-color: ${Colors.ORANGE}; */
`;

const RadioButtonsContainer = styled.div``;

export const QuizCreationForm: React.FC = () => {
  const [inputValues, dispatch] = useReducer(
    QuizCreationReducer,
    QuizCreationStartingValues
  );

  const handleChange = (input: string, inputName: InputName) => {
    dispatch({ type: inputName, payload: input });
  };

  type PrivacyChoice = "yes" | "no";
  const handleRadioButtonChange = (choice: PrivacyChoice) => {
    const actionToDispatch: QuizCreationAction = {
      type: "isPrivate",
      payload: false
    };
    if (choice === "yes") {
      actionToDispatch.payload = true;
    }
    dispatch(actionToDispatch);
  };

  const handleSubmit = () => console.log("Submitted", inputValues);

  console.log("INPUT VALUES", inputValues);
  return (
    <Container>
      <FormTitle>Create your quiz</FormTitle>
      <form
        onSubmit={(e: React.FormEvent<HTMLFormElement>) => {
          e.preventDefault();
          handleSubmit();
        }}
      >
        <Input inputName="title" onChangeFunction={handleChange} />
        <Input inputName="backgroundImageUrl" onChangeFunction={handleChange} />

        <RadioButtonsContainer>
          <StyledLabel>Do you want to keep the quiz private?</StyledLabel>
          <input
            type="radio"
            id="yes"
            name="isPrivate"
            value="yes"
            onChange={e =>
              handleRadioButtonChange(e.target.value as PrivacyChoice)
            }
          />
          <label htmlFor="yes">YES</label>
          <input
            type="radio"
            id="no"
            name="isPrivate"
            value="no"
            onChange={e =>
              handleRadioButtonChange(e.target.value as PrivacyChoice)
            }
          />
          <label htmlFor="no">NO</label>
        </RadioButtonsContainer>

        {/* <div>
          <input type="radio" id="dewey" name="isPrivate" value="dewey" />
          <label htmlFor="dewey">Dewey</label>
        </div> */}
        <Input inputName="questions" onChangeFunction={handleChange} />
        <SubmitButton type="submit">SUBMIT</SubmitButton>
      </form>
    </Container>
  );
};
