import React, { useContext } from "react";
import styled from "styled-components";
import { MainTitle } from "../UI/MainTitle";
import { Step } from "../../models/Step";
import { QuizType, Subject } from "../../models/Question";
import {
  shortenLengthySubjects,
  getSubjectBackgroundPic
} from "../../utils/functions";
import {
  ProgrammingCategories,
  TriviaCategories
} from "../../constants/questionCategories";
import { Colors } from "../../constants/colors";
import { QuizzesContext } from "../../contexts/quizzes/Quizzes";

const SubjectsContainer = styled.div`
  flex: 1;
  display: flex;
  padding: 0 3rem;
  flex-wrap: wrap;
  justify-content: space-between;

  ::after {
    content: "";
    flex: auto;
  }
`;

const StyledSubject = styled.div`
  padding: 1rem;
  cursor: pointer;
  padding: 0.5rem 1rem;
  border-radius: 10px;
  text-transform: uppercase;
  background-color: ${Colors.DARK_BLUE};
  font-size: 1.6rem;
  width: 200px;
  height: 200px;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  margin: 2px;
  background-position: center;
  background-size: cover;

  :hover {
    transform: scale(1.02);

    h3 {
      background-color: ${Colors.YELLOW};
      color: white;
    }
  }
`;

const Title = styled.h3`
  background-color: white;
  color: ${Colors.BLACK};
  padding: 0 0.5rem;
  font-size: 1.2rem;
`;

const BigTitle = styled(MainTitle)`
  margin-bottom: 0.6rem;
`;

const SubTitle = styled.p`
  color: ${Colors.LIGHTER_GREY};
  margin-bottom: 2rem;
  font-size: 1.1rem;
`;

const SubjectElement: React.FC<{ title: string; value: Subject } & Step> = ({
  title,
  value,
  goForward
}) => {
  const { configQuiz, goToNextQuizConfiguration } = useContext(QuizzesContext);
  return (
    <StyledSubject
      onClick={() => {
        configQuiz("subject", value);
        goToNextQuizConfiguration();
      }}
      style={{ backgroundImage: `url("${getSubjectBackgroundPic(value)})"` }}
    >
      <Title>{title}</Title>
    </StyledSubject>
  );
};

interface Props {
  quizType: QuizType;
}

interface Updatable {
  updateSubject: (subject: Subject) => void;
}

export const SubjectsStep: React.FC<Props & Step> = ({
  quizType,
  goForward
}) => {
  const availableSubjects =
    quizType === QuizType.TRIVIA ? TriviaCategories : ProgrammingCategories;
  return (
    <>
      <BigTitle>{quizType.toUpperCase()} QUIZ</BigTitle>
      <SubTitle>
        Choose a topic{" "}
        <span role="img" aria-label="finger pointing down">
          ðŸ‘‡
        </span>
      </SubTitle>
      <SubjectsContainer>
        {availableSubjects
          .sort((a, b) => {
            if (a! < b!) {
              return -1;
            }
            if (a! > b!) {
              return 1;
            }
            return 0;
          })
          .map(subject => (
            <SubjectElement
              key={subject}
              value={subject as Subject}
              title={shortenLengthySubjects(subject as string)!}
              goForward={goForward}
            />
          ))}
        <SubjectElement value="" title="Random" goForward={goForward} />
      </SubjectsContainer>
    </>
  );
};

